package lcp57

import "testing"

func runSample(t *testing.T, moles [][]int, expect int) {
	res := getMaximumNumber(moles)

	if res != expect {
		t.Fatalf("Sample expect %d, but got %d", expect, res)
	}
}

func TestSample1(t *testing.T) {
	moles := [][]int{
		{1, 1, 0}, {2, 0, 1}, {4, 2, 2},
	}
	expect := 2
	runSample(t, moles, expect)
}

func TestSample2(t *testing.T) {
	moles := [][]int{
		{2, 0, 2}, {5, 2, 0}, {4, 1, 0}, {1, 2, 1}, {3, 0, 2},
	}
	expect := 3
	runSample(t, moles, expect)
}

func TestSample3(t *testing.T) {
	moles := [][]int{
		{0, 1, 0}, {0, 0, 1},
	}
	expect := 0
	runSample(t, moles, expect)
}

