package p1044

import (
	"reflect"
	"sort"
	"testing"
)

func runSample(t *testing.T, str string, expect string) {
	res := longestDupSubstring(str)

	if res != expect {
		t.Errorf("Sample %s, expect %s, but got %s", str, expect, res)
	}
}

func TestSample1(t *testing.T) {
	runSample(t, "banana", "ana")
}

func TestSample2(t *testing.T) {
	runSample(t, "abcd", "")
}

func TestSample3(t *testing.T) {
	runSample(t, "", "akyj")
}

func checkSuffixTreeNode(t *testing.T, text string, suffixs []string) {
	root := buildSuffixTree(text)

	if root == nil {
		t.Errorf("Get nil tree root")
		return
	}

	ss := getSuffixsFromTree(root, text)

	sort.Strings(ss)
	sort.Strings(suffixs)

	if !reflect.DeepEqual(ss, suffixs) {
		t.Errorf("Sample %s, expect %v, but got %v", text, suffixs, ss)
	}
}

func TestSuffixTree1(t *testing.T) {
	text := "abcabxabcd$"
	var expect []string

	for i := 0; i <= len(text)-1; i++ {
		expect = append(expect, text[i:])
	}

	checkSuffixTreeNode(t, text, expect)
}

func TestSuffixTree2(t *testing.T) {
	text := "$"
	var expect []string

	for i := 0; i <= len(text)-1; i++ {
		expect = append(expect, text[i:])
	}

	checkSuffixTreeNode(t, text, expect)
}
