package p1930

import "testing"

func runSample(t *testing.T, num string, expect bool) {
	res := sumGame(num)

	if res != expect {
		t.Errorf("Sample expect %t, but got %t", expect, res)
	}
}

func TestSample1(t *testing.T) {
	num := "25??"
	expect := true
	runSample(t, num, expect)
}

func TestSample2(t *testing.T) {
	num := "?3295???"
	expect := false
	runSample(t, num, expect)
}

